			} elseif ($fecharobo!="--" && $cont == true ) {
				$q.= " AND anu_data_robatori LIKE '%$fecharobo%'";
			}

			if ($numeroserie!="" && $cont == false) {
				$q.= "WHERE anu_numero_serie LIKE '%$numeroserie%'";
				$cont = true;
			} elseif ($numeroserie!="" && $cont == true ) {
				$q.= " AND anu_numero_serie LIKE '%$numeroserie%'";
			}


			$query = mysqli_query($conexion, $q);

			if (mysqli_num_rows($query)>0) {
